package com.leetcode.medium.string;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class _438_Find_All_Anagrams_in_a_String {
    //https://leetcode.com/problems/find-all-anagrams-in-a-string/

    public List<Integer> findAnagrams(String s, String p) {
        List<Integer> l = new ArrayList<>();

        Map<Character, Integer> smap = new HashMap<>();
        for (int i = 0; i < p.length(); i++) {
            smap.put(s.charAt(i), smap.getOrDefault(s.charAt(i), 0) + 1);
        }

        Map<Character, Integer> pmap = new HashMap<>();
        for (char ch: p.toCharArray()) pmap.put(ch, pmap.getOrDefault(ch, 0) + 1);

        if (check(smap, pmap)) {
            l.add(0);
        }

        for (int i = p.length(); i < s.length(); i++) {
            int prevFirstCharacter = i - p.length();
            char prevCh = s.charAt(prevFirstCharacter);
            if (smap.get(prevCh) == 1) {
                smap.remove(prevCh);
            } else {
                smap.put(prevCh, smap.get(prevCh) - 1);
            }
            smap.put(s.charAt(i), smap.getOrDefault(s.charAt(i), 0) + 1);

            if (check(smap, pmap)) {
                l.add(i - p.length() + 1);
            }
        }

        return l;
    }

    private boolean check(Map<Character, Integer> smap, Map<Character, Integer> pmap) {
        if (smap.size() != pmap.size()) return false;
        if (!smap.keySet().containsAll(pmap.keySet())) return false;

        boolean not = true;
        for (var e: smap.entrySet()) {
            Integer sch = e.getValue();
            Integer pch = pmap.get(e.getKey());
            if (!sch.equals(pch)) {
                not = false;
                break;
            }
        }
        return not;
    }

    public static void main(String[] args) {
        var solution = new _438_Find_All_Anagrams_in_a_String();
//        String s = "cbaebabacd";
//        String p = "abc";
//        String s = "aa";
//        String p = "bb";

        String s = "";
        String p = "";
        System.out.println(solution.findAnagrams(s, p));
    }
}
